using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace DailyDoseCSharp
{
	public class Day6
	{
		public static int Q1()
		{
			int count = 0;
			var map = new Dictionary<char, bool>();
			for (var i = 0; i < rawInput.Length; i++)
			{
				char character = rawInput[i];
				if (map.ContainsKey(character))
				{
					count = 1;
					map.Clear();
				}
				else
				{
					count++;
					if (count == 4)
					{
						return i + 1;
					}
				}
				map.Add(character, true);
			}
			return 0;
		}


		public static int Q2()
		{
			return 0;
		}

		static string rawInput = @"";
	}
}
